mapper.select:
select

from fangjian_t where 1=1 

    and fj_hao LIKE concat('%',#{fjHao},'%')


    and state = #{state}

order by id limit #{beginRow},#{pageSize}

select count(1) from fangjian_t where 1=1
and fj_hao LIKE concat('%',#{fjHao},'%')
and state = #{state}

select
count(1)
from fangjian_t where fj_hao = #{fjHao,jdbcType=VARCHAR} and id != #{id,jdbcType=INTEGER}
