mapper.select:
select

from fangjian_t where 1=1 

    and fjhao LIKE concat('%',#{fjHao},'%')


    and state = #{state}

order by id limit #{beginRow},#{pageSize}

select count(1) from fangjian_t where 1=1
and fjhao LIKE concat('%',#{fjHao},'%')
and state = #{state}

mapper.update:
update fangjian_t set 
  state = #{state,jdbcType=VARCHAR} 
where id = #{id,jdbcType=INTEGER}

mapper.select:
select
count(1)
from fangjian_t where fjhao = #{fjHao,jdbcType=VARCHAR} and id != #{id,jdbcType=INTEGER}

select
count(1)
from fangjian_t where jibie = #{jiBie,jdbcType=VARCHAR}
